var it = require('../it-is')
  , assert = require('assert')

exports ['chained assertions return nice toString()'] = function (){

//  console.log(it.ok())

  var examples = 
  [ [ it.ok(),'it.ok()'] 
  , [ it.ok().isInstanceof(Error),'it.ok().isInstanceof(function Error() {...})'] 
  , [ it.ok(null),'it.ok(null)']
  , [ it.every(it.ok()),'it.every(it.ok())']
  , [ it.ok().isInstanceof(Error).has({ stack: it.typeof('string')} )
    , 'it.ok().isInstanceof(function Error() {...}).has({ stack: it.typeof("string") })'] 
  ]
  examples.forEach(function (e){
    var v = e[0].toString()
    console.log(v)
    assert.equal(v, e[1])
  })
}
